INSERT INTO movie (id, name, description) VALUES (1, 'Doctor Strange in the Multiverse of Madness', 'Dr Stephen Strange casts a forbidden spell that opens a portal to the multiverse. However, a threat emerges that may be too big for his team to handle.');
INSERT INTO movie (id, name, description) VALUES (2, 'Ms Marvel', 'Kamala is a superhero fan with an imagination, particularly when it comes to Captain Marvel; Kamala feels like she does not fit in at school and sometimes even at home, that is until she gets superpowers like the heroes she is looked up to.');
INSERT INTO movie (id, name, description) VALUES (3, 'Thor: Love and Thunder', 'Thor embarks on a journey unlike anything he is ever faced -- a quest for inner peace. However, his retirement gets interrupted by Gorr the God Butcher, a galactic killer who seeks the extinction of the gods.');